import java.util.Scanner; public class CF10B {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int k = sc.nextInt();		boolean[][] seat = new boolean[k][k];		int[][] value = new int[k][k];		int center = k / 2;		for (int i = 0; i < k; i++) {			for (int j = 0; j < k; j++) {				value[i][j] = Math.abs(i - center) + Math.abs(j - center);			}		}		for (int i = 0; i < n; i++) {			int num = sc.nextInt();			int min = Integer.MAX_VALUE;			int mx = -1;			int ml = -1;			for (int x = 0; x < k; x++) {				for (int l = 0; l < k - num + 1; l++) {					boolean ok = true; 					for (int j = l; j < l + num; j++) {						if (seat[x][j]) {							ok = false;							break;						}					}					if (!ok) {						continue;					}					int cur = 0;					for (int j = l; j < l + num; j++) {						cur += value[x][j];					}					if (cur < min || (cur == min && x < mx)							|| (cur == min && x == mx && l < ml)) {						min = cur;						mx = x;						ml = l;					}				}			}			if (min == Integer.MAX_VALUE) {				System.out.println(-1);			} else {				System.out						.println((mx + 1) + " " + (ml + 1) + " " + (ml + num));				for (int j = ml; j < ml + num; j++) {					seat[mx][j] = true;				}			}		}		sc.close();	}}
