import java.util.*;public class MyClass {    public static void main(String args[]) {     Scanner sc=new Scanner(System.in);     ArrayList<Integer> a=new ArrayList<Integer>();     int n=sc.nextInt();     int[] arr=new int[n];     for(int i=0;i<n;i++)     arr[i]=sc.nextInt();     HashMap<Integer,Integer> h=new HashMap<Integer,Integer>();     for(int i=0;i<n;i++)     {         if(h.containsKey(arr[i]))         {             int count=h.get(arr[i]);             h.put(arr[i],count+1);         }         else         h.put(arr[i],1);     }     int max=(Collections.max(h.values()));      System.out.println(max);    }}
