import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int d=sc.nextInt();        int[] a=new int[n];        for(int i=0;i<n;i++)        {            a[i]=sc.nextInt();        }        int count=2;        for(int i=0;i<n-1;i++)        {            if((a[i+1]-a[i])>(2*d))            {                count=count+2;            }            else if(a[i+1]-a[i]==2*d)            {                count=count+1;            }            else            {                continue;            }        }        System.out.println(count);    }}
