#include <bits/stdc++.h>using namespace std;typedef long long int ll;int main(){    ll l,k,x1=0,i,j,a[30]={0};    string s;    cin>>s;    cin>>k;    l=s.size();    map<int,int>b;    ll k1=1;    if(k<l)    {    for(i=0; s[i]!='\0'; i++)    {        a[s[i]-96]=a[s[i]-96]+1;    }    ll p,sum=0,t;    while(1)    {        p=2e18;        for(j=1; j<27; j++)        {            if(a[j]<p)            {                p=sum+a[j];                t=sum+j;            }        }        if(p>k)        {            break;        }        else        {            k=k-p;          a[t]=2e18;        }    }    for(i=1; i<=26; i++)    {        if(a[i]!=2e18)        {            x1++;        }    }    cout<<x1<<endl;    for(i=0; s[i]!='\0'; i++)    {        if(a[s[i]-96]!=2e18)        {            cout<<s[i];        }    }    }    else        cout<<"0";}  
