import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int m=input.nextInt();            int n=input.nextInt();            int arr[]=new int[n];            int flag=0;            for(int i=0;i<n;i++)            {                out.println(1);                out.flush();                int v=input.nextInt();                if(v==0)                {                    flag=1;                    break;                }                else if(v==-1)                {                    arr[i]=0;                }                else                {                    arr[i]=1;                }            }            if(flag==0)            {                int i=1,j=m+1;                int c=0;                while(true)                {                    int mid=(i+j)/2;                    out.println(mid);                    out.flush();                    int v=input.nextInt();                    int in=c%n;                    if(arr[in]==0)                    {                        if(v==1)                        {                            v=-1;                        }                        else if(v==-1)                        {                            v=1;                        }                    }                    if(v==-1)                    {                        j=mid;                    }                    else if(v==1)                    {                        i=mid+1;                    }                    else                    {                        break;                    }                    c++;                }            }        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str="";            try            {                str=br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
