#include <iostream> using namespace std; int n, m, ans[50]; int main(){	cin >> m >> n;	for(int i=1; i<=n; i++)		{	cout << 1 << '\n';			cin >> ans[i];			if(!ans[i])				return 0;		}	int st = 1, dr = m, sol = 0, curr = 1;	while(st <= dr)		{	int mij = (st + dr) / 2, x;			cout << mij << '\n';			cin >> x;			if(!x)				return 0;			if(x != ans[curr]) sol = mij, dr = mij - 1;			else st = mij + 1;			curr++;			if(curr == n + 1)				curr = 1;		}	return 0;}
