//import java.lang.*;import java.util.*; public class Main { 	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int n = scanner.nextInt(); 		long[] arr = new long[2 * n];		for (int i = 0; i < 2 * n; ++i)			arr[i] = scanner.nextInt();		Arrays.sort(arr);		long ans = (arr[n-1] - arr[0]) * (arr[2 * n - 1] - arr[n]);		for (int i = n; i < 2 * n; ++i)		{			ans = Math.min(ans,					(arr[2 * n - 1] - arr[0]) * (arr[i] - arr[i - n + 1]));		}		if (n <= 1)			ans = 0;		System.out.println(ans);		scanner.close();	} // end main	}
