import java.util.*;public class Main{	public static void main(String[] args)	{	    int n,m,count=0;;	    Scanner in=new Scanner(System.in);	    Set<Integer> set=new HashSet<Integer>();	    n=in.nextInt();	    m=in.nextInt();	    int l[]=new int[n];	    int r[]=new int[n];	    for(int i=0;i<n;i++)	    {	        l[i]=in.nextInt();	        r[i]=in.nextInt();	        for(int j=l[i];j<=r[i];j++)	        {	            set.add(j);	        }	    }	    for(int i=1;i<=m;i++)	    {	        if(!set.contains(i))	        {	            count++;	        }	    }	    System.out.println(count);	    for(int i=1;i<=m;i++)	    {	        if(!set.contains(i))	        {	            System.out.print(i+" ");	        }	    }	}}
