import java.io.*;import java.util.*;import java.text.*;import java.math.*;import java.util.regex.*;public class Main{    public static void main(String[] args) {        Scanner s=new Scanner(System.in);        int n=s.nextInt(),t=0;        int[] a=new int[n];        for(int i=0;i<n;i++)        {            int e=s.nextInt(),g=s.nextInt(),m=s.nextInt(),h=s.nextInt();            a[i]=e+g+m+h;        }        t=a[0];        Arrays.sort(a);        for(int i=n-1;i>=0;i--)        if(t==a[i])        {            t=i;            break;        }        System.out.println(n-t);    }}
