import java.util.Scanner;// import java.io.BufferedReader;// import java.io.OutputStream;// import java.io.BufferedOutputStream;// import java.util.Arrays;public class QuickStart {  public static void main(String args[]) {    Scanner sc = new Scanner(System.in);    int n = sc.nextInt();    int m = sc.nextInt();    int[] prices = new int[101];    int[][] matchingPairs = new int[101][101];    int answer = 100000000;     for (int i = 1; i <= n; i++) prices[i] = sc.nextInt();     for (int i = 1; i <= n; i++){      for (int j = 1; j <= n; j++) matchingPairs[i][j] = 0;    }     for (int i = 1; i <= m; i++){      int a = sc.nextInt();      int b = sc.nextInt();      matchingPairs[a][b] = 1;      matchingPairs[b][a] = 1;    }     for(int i = 1; i <= n; i++){      for(int j = 1; j <= n; j++){        for(int k = 1; k <= n; k++){          if(matchingPairs[i][j] != 0 && matchingPairs[j][k] != 0 && matchingPairs[k][i] != 0){            if(answer > prices[i] + prices[j] + prices[k]) answer = prices[i] + prices[j] + prices[k];          }        }      }    }     if (answer == 100000000) answer = -1;     System.out.println(answer);  }}  	   		  	   	 	  	  	 	 	 	 	
