#!/usr/bin/env python3 import sys srci = sys.stdin n, m = map(int, srci.readline().rstrip().split())prices = list(map(int, srci.readline().rstrip().split()))match = [[False for i in range(n+1)] for i in range(n+1)]for i in range(m):    u, v = map(int, srci.readline().rstrip().split())    match[u][v] = match[v][u] = True mn = -1for i in range(1, n+1):    for j in range(i+1, n+1):        if not match[i][j]:            continue        for k in range(j+1, n+1):            if match[j][k] and match[k][i]:                t = prices[i-1] + prices[j-1] + prices[k-1]                if mn == -1 or t < mn:                    mn = t print(mn)
