import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int h=sc.nextInt();        int a=sc.nextInt();        int b=sc.nextInt();        int k=sc.nextInt();        for(int i=0;i<k;i++)        {            int res=0;            int ta=sc.nextInt();            int fa=sc.nextInt();            int tb=sc.nextInt();            int fb=sc.nextInt();            if(ta==tb)            {                if(fb>fa)                {                    res=fb-fa;                }                else                {                    res=fa-fb;                }            }            else             {                if(tb>ta)                {                    res=res+(tb-ta);                }                else                {                    res=res+(ta-tb);                }                if(fa>b || fa<a)                {                    int s=0;                    if(fa>b)                    {                        res=res+(fa-b);                        s=b;                    }                    if(fa<a)                    {                        res=res+(a-fa);                        s=a;                    }                    if(fb>s)                    {                        res=res+(fb-s);                    }                    else                    {                        res=res+(s-fb);                    }                }                else                {                    if(fb>fa)                    {                        res=res+(fb-fa);                    }                    else                    {                        res=res+(fa-fb);                    }                }            }            System.out.println(res);        }    }  } 
