import java.util.*;import java.lang.Math.*;public class Main{	public static void main(String[] args) {		Scanner sc=new Scanner(System.in);		String a=sc.nextLine();		int t=Integer.parseInt(a);		while(t>0)		{		    String b=sc.nextLine();		    int n=Integer.parseInt(b);		    String s=sc.nextLine();		    int c=0;		    for(int i=0;i<s.length()/2;i++)		    {		        if((Math.abs(s.charAt(i)-s.charAt(s.length()-1-i))!=0)&&(Math.abs(s.charAt(i)-s.charAt(s.length()-1-i))!=2))		        {		            c=1;		            break;		        }		    }		    if(c==1)		    {		        System.out.println("NO");		    }		    else		    {		        System.out.println("YES");		    }		    t--;		}	}}
