import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int L1=-1,R1=-1,L2=-1,R2=-1;        int n=sc.nextInt();        int m=sc.nextInt();        String[] table =new String[n];        for(int i=0;i<n;i++)        {            table[i]=sc.next();        }        for(int i=0;i<n;i++)        {            for(int j=0;j<m;j++)            {                if(table[i].charAt(j)=='B')                {                    if(L1==-1)                    {                        L2=i;                        L1=i;                        R2=j;                        R1=j;                    }                    else if(i>L2 && j>R2)                    {                        L2=i;                        R2=j;                    }                }            }        }        System.out.println((L1+L2+2)/2 +" "+(R1+R2+2)/2);            }}
