#!/usr/bin/python3 import sysimport argparseimport jsonimport math def main():    #number, subtract_count = sys.stdin.readline().split(" ")    n, d = [int(x) for x in sys.stdin.readline().split(" ")]    num_grasshoppers = int(sys.stdin.readline())    grasshopper_locations = []    for i in range(num_grasshoppers):        x, y = [int(x) for x in sys.stdin.readline().split(" ")]        grasshopper_locations.append((x, y))     x_dist = d*math.sqrt(2)/2    y_dist = (abs(n-d))*math.sqrt(2)/2    center = (n/2, n/2)    for location in grasshopper_locations:        t_x, t_y = translate_coordinate(center, location)         if abs(int(t_x*10000)) <= int(x_dist*10000) and abs(int(t_y*10000)) <= int(y_dist*10000):            print("YES")        else:            print("NO")        #print("Location: (%r, %r)" % (location[0], location[1]))        #print("(%r, %r) vs %r %r" % (t_x, t_y, x_dist, y_dist)) def translate_coordinate(center, coordinate):    tc = (coordinate[0] - center[0], coordinate[1] - center[1])    rotate_degrees = 45*math.pi/180    rc_x = tc[0] * math.cos(rotate_degrees) - tc[1] * math.sin(rotate_degrees)    rc_y = tc[0] * math.sin(rotate_degrees) + tc[1] * math.cos(rotate_degrees)     return (rc_x, rc_y) def get_tests():    #tests = [("512 4", "50")]    tests = [("7 2\n4\n2 4\n4 1\n6 3\n4 5", "YES\nNO\nNO\nYES"),             ("8 7\n4\n4 4\n2 8\n8 1\n6 1", "YES\nNO\nYES\nYES")]     for test in tests:        json_string = json.dumps({"input": test[0], "output": test[1]})        print(json_string) if __name__ == "__main__":    parser = argparse.ArgumentParser()    parser.add_argument("--get-tests", action="store_true")    args = parser.parse_args()     if args.get_tests:        get_tests()    else:        main()
