#include <vector>#include <iostream>typedef long double ll; using namespace std;bool isinside(ll x, ll y, ll x1, ll y1, ll x2, ll y2, ll x3, ll y3){    x -= x1; y -= y1;    x2 -= x1; y2 -= y1;    x3 -= x1; y3 -= y1;    if (x / y == x2 / y2 || x / y == x3 / y3)        return true;    ll m, l;    m = (x * y2 - x2 * y) / (x3 * y2 - x2 * y3);    if (m >= 0 && m <= 1)    {        l = (x - m * x3) / x2;        if (l >= 0 && (m + l) <= 1)            return true;    }    return false;}     int main(){    ll d, n, m;    vector <pair<ll, ll>> kuz;    cin >> n >> d >> m;    for (int i = 0; i < m; i++)    {        ll x, y;        cin >> x >> y;        kuz.push_back(make_pair(x, y));     }    for (int i = 0; i < m; i++)    {        if (isinside(kuz[i].first, kuz[i].second, 0, d, d, 0, n - d, n) || isinside(kuz[i].first, kuz[i].second, n, n - d, d, 0, n - d, n))            cout << "YES" << endl;        else cout << "NO" << endl;    } }
