#include <bits/stdc++.h>using namespace std;#define nl "\n"#define all(cont) cont.begin(), cont.end()typedef long long ll;typedef pair<int, int> pii;const ll N = 50005;const ll mod = (1e9) + 7;ll gcd(ll a, ll b) { return ((b == 0) ? a : gcd(b, a % b)); }void solve(){    int n, i, a, b;    string s;    cin >> s;    n = s.length();    vector<pii> v(101, {0, 0});    for (i = 1; i <= 100; i++)    {        for (int j = 1; j <= i; j++)        {            if (i % j == 0)            {                a = j;                b = i / j;                if (a <= 5 && b <= 20)                {                    v[i].first = a;                    v[i].second = b;                    break;                }            }        }    }    int row = 101, col = 101;    // for (i = 1; i <= 100; i++)    //     cout << i << " " << v[i].first << " " << v[i].second << nl;    for (i = n; i <= 100; i++)    {        if (v[i].first > 0)            row = min(row, v[i].first);    }    for (i = n; i <= 100; i++)    {        if (v[i].first == row)            col = min(col, v[i].second);    }    cout << row << " " << col << nl;    a = 1;    b = row * col;    n = b - n;    for (auto x : s)    {        cout << x;        a++;        if (a == col && n > 0)        {            cout << "*" << nl;            n--;            a = 1;        }        else if (a > col)        {            cout << nl;            a = 1;        }    }}int main(){    // Fast I/O    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);    // Files#ifndef ONLINE_JUDGE    freopen("input.txt", "r", stdin);    freopen("output.txt", "w", stdout);#endif    ll t = 1;    // cin >> t;    while (t--)    {        solve();    }    return 0;}
