//Code by Sounak, IIESTimport java.io.*;import java.math.*;import java.util.*;import java.util.Arrays;public class Test1{    public static void main(String args[])throws IOException{        Scanner sc=new Scanner(System.in);        char ch[]=sc.next().toCharArray();        int i,j,r=1,c=1,n;        n=ch.length;        for(i=1;i<=5;i++)        {            r=i;            c=(int)(Math.ceil((double)n/r));            if(c<=20)            break;        }        int ast=r*c-n;        int initial=ast/r;        int fin=ast%r;        int index=0;        System.out.println(r+" "+c);        for(i=0;i<r;i++)        {            for(j=0;j<initial;j++)            System.out.print("*");            if(fin>0)            {                System.out.print("*");                fin--;                j++;            }                        for(;j<c;j++)            {                System.out.print(ch[index]);                index++;            }            System.out.println();        }    }}
