import java.util.Scanner;public class Tin{    public static void main(String []args)    {            Scanner tin=new Scanner(System.in);        int t=tin.nextInt();                for(int x=0;x<t;x++)        {            boolean rhj=true;            long a=tin.nextLong();            long b=tin.nextLong();                        long y=a+b;                        for(int z=2;z<=Math.sqrt(y);z++)            {               if(y%z==0)               {                    rhj=false;               }            }            if(rhj&&a-b==1)            {                System.out.println("Yes");            }            else            {                System.out.println("No");            }        }            }}
