import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int n=input.nextInt();            int a[]=new int[n];            int m=0;            int g=0;            for(int i=0;i<n;i++)            {                a[i]=input.nextInt();                g=gcd(g,a[i]);                m=Math.max(m,a[i]);            }            int arr[]=new int[m+1];            for(int i =0; i <= m; i++) arr[i] = i;            for(int i=2; i*i<=m; i++)            {                if(arr[i]==i)                {                    long x=(long)i*i;                    while(x<=m)                    {                        if(arr[(int)x]==x)                        {                            arr[(int)x]=i;                        }                        x+=i;                    }                }            }            int set[]=new int[m+1];            for(int i=0;i<n;i++)            {                a[i]/=g;                int v=a[i];                int pre=0;                while(true)                {                    int f=arr[v];                    if(f!=pre)                    {                        set[f]++;                        pre=f;                    }                    if(v==1)                    {                        break;                    }                    v/=f;                }            }            int min=Integer.MAX_VALUE;            for(int i=2;i<=m;i++)            {                if(set[i]!=0)                {                    if(set[i]<n)                    {                        min=Math.min(min,n-set[i]);                    }                }            }            if(min==Integer.MAX_VALUE)            {                min=-1;            }            out.println(min);        }        out.close();    }    public static int gcd(int a, int b)    {        if(a==0)        {            return b;        }        else        {            return gcd(b%a,a);        }    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str="";            try            {                str=br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
