#include <bits/stdc++.h>using namespace std; #define int long long int n,m; signed main(){	cin>>n>>m;	if(n>m)		swap(n,m);	if(n==1)	{		if(m%6==0)			cout<<m<<endl;		else if(m%6<=3)			cout<<m-m%6<<endl;		else			cout<<m-(6-m%6)<<endl;	}	else if(n== 2 && m==2)		cout<<0<<endl;	else if(n== 2 && m==3)		cout<<4<<endl;	else if(n== 2 && m==7)		cout<<12<<endl;	else if(n%2==1 && m%2==1)		cout<<n*m-1<<endl;	else		cout<<n*m<<endl;		return 0;}
