import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        String ans="";        if(n==1 || n==2){            ans="No";            System.out.println(ans);        }        else{            ans="Yes";            System.out.println(ans);            System.out.print(n/2+" ");            for(int i=1;i<=n;i++){                if(i%2==0)                    System.out.print(i+" ");            }            System.out.println();            System.out.print(n-n/2+" ");            for(int j=1;j<=n;j++){                if(j%2!=0)                    System.out.print(j+" ");            }        }            }}
