import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        int a=s.nextInt();        int b=s.nextInt();        int d[]=new int[n];        for(int i=0;i<n;i++)            d[i]=s.nextInt();        int min=Math.min(a,b);         int j=0,k=n-1,ans=0;        while(j<=k){            boolean v=check(d,j,k);            boolean v1=check1(d,j,k);                        if(v==false){                ans=-1;                break;            }            else if(v1==true){                if(d[j]==d[k] && d[k]==2){                    if(j==k)                        ans=ans+min;                    else if(j<k)                        ans=ans+min*2;                }                            else{                    if(d[j]==0 || d[k]==0)                        ans=ans+a;                    else if(d[j]==1 || d[k]==1)                        ans=ans+b;                }            }            j++;            k--;                    }        System.out.println(ans);            }    public static boolean check(int[] d,int j,int k){        boolean v=true;        if((d[j]==0 && d[k]==1) || (d[j]==1 && d[k]==0)){            v=false;        }        return v;    }    public static boolean check1(int[] d,int j,int k){        boolean v=true;        if((d[j]==0 && d[k]==0) || (d[j]==1 && d[k]==1)){            v=false;        }        return v;    }}   
