//stan hu taoimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import java.util.*;import java.io.*;import java.math.*;Â public class x1042A{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(infile.readLine());        int N = Integer.parseInt(st.nextToken());        int M = Integer.parseInt(infile.readLine());        int[] arr = new int[N];        for(int i=0; i < N; i++)            arr[i] = Integer.parseInt(infile.readLine());        int max = arr[0];        for(int x: arr)            max = max(max, x);        max += M;        while(M-->0)        {            int best = 0;            for(int i=1; i < N; i++)                if(arr[i] < arr[best])                    best = i;            arr[best]++;        }        int min = arr[0];        for(int x: arr)            min = max(min, x);        System.out.println(min+" "+max);    }    public static int[] readArr(int N, BufferedReader infile, StringTokenizer st) throws Exception    {        int[] arr = new int[N];        st = new StringTokenizer(infile.readLine());        for(int i=0; i < N; i++)            arr[i] = Integer.parseInt(st.nextToken());        return arr;    }}
