import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int n=input.nextInt();            int m=input.nextInt();            long sum=0;            for(int i=0;i<m;i++)            {                for(int j=0;j<m;j++)                {                    if((i*i+j*j)%m==0)                    {                        int r=n%m;                        int dif=r-i;                        if(dif<0)                        {                            dif+=m;                        }                        int v=n-dif;                        int x=i;                        if(x==0)                        {                            x+=m;                        }                        int s1=(v-x)/m+1;                        dif=r-j;                        if(dif<0)                        {                            dif+=m;                        }                        v=n-dif;                        x=j;                        if(x==0)                        {                            x+=m;                        }                        int s2=(v-x)/m+1;                        sum+=(long)s1*(long)s2;                    }                }            }            out.println(sum);        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str = "";            try            {                str = br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
