import java.util.*;public class water{    public static void main(String args[])    {        Scanner ob=new Scanner(System.in);        int n=ob.nextInt();        long a[]=new long[n+1];        long sum=0;        for(int i=1;i<=n;i++)        {            sum=sum+ob.nextInt();            a[i]=sum;        }        long max=0;        for(int i=0;i<=n;i++)            for(int j=i;j<=n;j++)                if(a[j]-a[i]>100*(j-i))                    max=Math.max(max,j-i);        System.out.println(max);    }}
