//package Coding; import java.io.*;import java.util.*; public class CodeForces {    public static void main(String[] args) throws IOException {        Scanner in = new Scanner(System.in);        long n = in.nextLong();        long N = n;        int lastD = 0;        int len = 0;        int ans = 0;         while (n > 0) {            lastD = (int) (n % 10);            len++;            n /= 10;        }         long n1 = (long)Math.pow(10, len - 1) * lastD - 1;        long n2 = N - n1;        while (n1 != 0 || n2 != 0) {            ans += n1 % 10 + n2 % 10;            n1 /= 10;            n2 /= 10;        }        System.out.println(ans);     }  }          
