import java.io.*;import java.util.*;public class ACM {    public static void main(String args[]) throws FileNotFoundException, IOException {        Scanner x = new Scanner(System.in);        int n = x.nextInt();        int s = x.nextInt();        int count = 1;        int arr[] = new int[n];        if (n >= s) {            System.out.print(1);        } else {            for (int i = 0; i < n; i++) {                arr[i] = count++;            }            if (s % arr[arr.length-1]!=0) {                System.out.print((s/arr[arr.length-1])+1);            }            else                System.out.print((s/arr[arr.length-1]));                   }    }}
