import java.io.*;import java.util.*;public class isolatedVertices {	public static void main(String[] args) throws IOException {		BufferedReader f = new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st = new StringTokenizer(f.readLine());		long n = Long.parseLong(st.nextToken());		long m = Long.parseLong(st.nextToken());		System.out.print(Math.max(0, n - 2L * m) + " ");		int max = (int) Math.ceil(0.5 + Math.pow(1L + 8L * m, 0.5) * 0.5);		if(m == 0) max = 0;		System.out.println(Math.max(n - max, 0));	}}
