//package div83;import java.io.*;import java.util.*;public class B {	public static void main(String args[])throws IOException	{		BufferedReader in=new BufferedReader(new InputStreamReader(System.in));		int n=Integer.parseInt(in.readLine());		StringTokenizer st=new StringTokenizer(in.readLine());		long a[]=new long[n];		for(int i=0;i<n;i++)			a[i]=Integer.parseInt(st.nextToken());		Arrays.sort(a);		String ans="NO";		for(int i=1;i<n;i++)		{			if(a[i-1]<a[i] && 2*a[i-1]>a[i])			{				ans="YES";				break;			}		}			System.out.println(ans);		}}
