import java.util.*;public class Main{	public static void main(String[] args) {		int a=2,    b=5,    c=8,    cont=0;        Scanner in =new Scanner(System.in);        int n,k,com;        n=in.nextInt();    k=in.nextInt();        a*=n;    b*=n;    c*=n;        com=0;    while(com<a)    {                com+=k;        cont++;    }        com=0;    while(com<b)    {        com+=k;        cont++;    }        com=0;    while(com<c)    {        com+=k;        cont++;    }        System.out.print(cont);    	}}
