import java.util.*;import java.lang.Math;public class Main{    public static void main (String[] args){        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        int[] hats = new int[n];        int[][] myArr = new int[3][n];        int available = 1;        for (int i = 0; i < n; i++){            int temp = scan.nextInt();            myArr[0][temp]++;            hats[i] = temp;        }        for (int i = 0; i < n; i++){            if (myArr[0][i] % (n - i) != 0){                System.out.println("Impossible");                return;            }        }        for (int i = 0; i < n; i++){            int hatNum = hats[i];            if (myArr[2][hatNum] == 0){                myArr[1][hatNum] = available++;            }            hats[i] = myArr[1][hatNum];            myArr[2][hatNum]++;            myArr[2][hatNum] %= n - hatNum;        }        if (available > n + 1){            System.out.println("Impossible");        }            System.out.println("Possible");        for (int x: hats)            System.out.print(x + " ");    }}
