//No sorceries shall prevail.import java.util.*;import java.io.*;public class Main { 	static Scanner inp= new Scanner(System.in);	static PrintWriter out= new PrintWriter(System.out);	public static void main(String args[]) {		    		   	Main g=new Main();	   	g.main();	   	out.close();	} 			//Main	void main() {				int n=inp.nextInt();		String s=inp.next();		int dp[]=new int[n];		dp[0]=s.charAt(0)=='G'?1:0;		long gg=dp[0];		long total=gg;		for(int i=1;i<n;i++) {			if(s.charAt(i)=='G') {				dp[i]=dp[i-1]+1;				total++;			}			gg=Math.max(gg, dp[i]);		}		//for(int i=0;i<n;i++) System.out.print(dp[i]+" ");		//System.out.println();		for(int i=n-2;i>=0;i--) {			if(dp[i]>0 && dp[i+1]>0) dp[i]=dp[i+1];		}		//for(int i=0;i<n;i++) System.out.print(dp[i]+" ");		if(dp[0]==0 && n>1) {			gg=Math.max(gg, 1+dp[1]);		}		if(dp[n-1]==0 && n>1) {			gg=Math.max(gg, 1+dp[n-2]);		}		for(int i=1;i<n-1;i++) {			if(dp[i]==0) gg=Math.max(gg, dp[i-1]+1+dp[i+1]);		}		System.out.println(Math.min(total,gg));	}				}
