//package codeforces.div2; import java.util.Scanner; public class VovaAndTrophies {    static public void print(String s) {         int count = 0;         for(int i = 0; i < s.length(); i++) {            if(s.charAt(i) == 'G') {                count++;            }        }         int left[] = new int[s.length()];        int right[] = new int[s.length()];         left[0] = s.charAt(0) == 'G' ? 1: 0;        for(int i = 1; i < s.length(); i++) {            if(s.charAt(i) == 'G') {                left[i] = left[i-1] + 1;            } else {                left[i] = 0;            }        }        right[s.length()-1] = s.charAt(s.length()-1) == 'G' ? 1 : 0;        for(int i = s.length() - 2; i>=0; i--) {            if (s.charAt(i) == 'G') {                right[i] = right[i+1] + 1;            } else {                right[i] = 0;            }        }        int maxTotal = 0;        for(int i = 0; i < s.length(); i++) {            maxTotal = Math.max(maxTotal, left[i]);            maxTotal = Math.max(maxTotal, right[i]);            if(i == 0) {                maxTotal = Math.max(maxTotal, right[i]);            } else {                if(i == s.length() - 1) {                    maxTotal = Math.max(maxTotal, left[i]);                } else {                    if(s.charAt(i) == 'S' && s.charAt(i-1)!='S' && s.charAt(i+1)!='S') {                        if(left[i-1] + right[i+1] == count) {                            maxTotal = Math.max(left[i-1] + right[i+1], maxTotal);                        } else {                            maxTotal = Math.max(left[i-1] + right[i+1] + 1 , maxTotal);                        }                    } else {                        if(s.charAt(i) == 'S' && s.charAt(i-1)!='S' && s.charAt(i + 1) == 'S' && left[i-1]!=count) {                            maxTotal = Math.max(maxTotal, left[i-1] + 1);                        } else {                            if(s.charAt(i) == 'S' && s.charAt(i-1) == 'S' && s.charAt(i+1) == 'G' && right[i+1]!=count) {                                maxTotal = Math.max(maxTotal, right[i + 1] + 1);                            }                        }                    }                }            }        }        System.out.print(maxTotal);    }     public static void main(String []args) {        Scanner sc = new Scanner(System.in);        int n  = sc.nextInt();        String s = sc.next();        print(s);    }}
