import java.util.Scanner;import java.util.Arrays;public class Main {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int n, sum = 0, minsum = Integer.MAX_VALUE, man = 0;        int[] a = new int[101];        n = in.nextInt();        for (int i = 1; i <= n; i++){            a[i] = in.nextInt();        }        for (int x = 1; x <= n; x++) {            sum = 0;            for (int i = 1; i <= n; i++){                man = 0;                man += 2 * (Math.abs(i-x)+ Math.abs(i-1) + Math.abs(1-x));            sum += man * a[i];            }            if (sum < minsum){                minsum = sum;            }        }        System.out.println(minsum);        in.close();    }}   			  	 		  	 	 				 		   	
