//import java.lang.*;import java.util.*; public class Main { 	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int n = scanner.nextInt();		int [] arr = new int[n];		for (int i = 0; i < n; ++i)		{			arr[i] = scanner.nextInt();		}				int sum = 0;				for(int i = 1; i < n; ++i)		{			for (int j = 0; j < arr[i]; ++j)			{				sum += i + i;			}		} 		for (int i = 1; i < n; ++i)		{			for (int j = 0; j < arr[i]; ++j)			{				sum += i + i;			}		}		System.out.println(sum);		scanner.close();	} // end main	}
