#!/usr/bin/env python3 vl, vr, pl, pr, k = map(int, input().split())lucky = [4, 7]sz = 0for i in range(1, 9):    base = 10 ** i    psz, sz = sz, len(lucky)    for j in [4 * base, 7 * base]:        for pos in range(psz, sz):            lucky.append(j + lucky[pos]) ans = 0for i in range(0, len(lucky)-k+1):    ll, lr = 1 if i == 0 else lucky[i-1] + 1, lucky[i]    rl, rr = lucky[i+k-1], 1_000_000_000 if i+k == len(lucky) else lucky[i+k] - 1    vc = max(0, min(vr, lr) - max(vl, ll) + 1)    pc = max(0, min(pr, rr) - max(pl, rl) + 1)    cnt1 = vc * pc    vc = max(0, min(pr, lr) - max(pl, ll) + 1)    pc = max(0, min(vr, rr) - max(vl, rl) + 1)    cnt2 = vc * pc    ans += cnt1 + cnt2    if k == 1 and cnt1 > 0 and cnt2 > 0:        ans -= 1 print(ans / ((vr - vl + 1) * (pr - pl + 1)))
