import java.io.OutputStream;import java.io.IOException;import java.io.InputStream;import java.io.PrintWriter;import java.util.StringTokenizer;import java.io.IOException;import java.io.BufferedReader;import java.io.InputStreamReader;import java.io.InputStream; /** * Built using CHelper plug-in * Actual solution is at the top * * @author Vadim */public class Main {    public static void main(String[] args) {        InputStream inputStream = System.in;        OutputStream outputStream = System.out;        FastScanner in = new FastScanner(inputStream);        PrintWriter out = new PrintWriter(outputStream);        A solver = new A();        solver.solve(1, in, out);        out.close();    }     static class A {        public void solve(int testNumber, FastScanner in, PrintWriter out) {            int y = in.ni(), b = in.ni(), r = in.ni();            b = Math.min(b, r - 1);            y = Math.min(y, b - 1);            out.println(y * 3 + 3);        }     }     static class FastScanner {        private BufferedReader in;        private StringTokenizer st;         public FastScanner(InputStream stream) {            in = new BufferedReader(new InputStreamReader(stream));        }         public String ns() {            while (st == null || !st.hasMoreTokens()) {                try {                    String rl = in.readLine();                    if (rl == null) {                        return null;                    }                    st = new StringTokenizer(rl);                } catch (IOException e) {                    throw new RuntimeException(e);                }            }            return st.nextToken();        }         public int ni() {            return Integer.parseInt(ns());        }     }}
