import java.util.Scanner;public class Main {    public static void main(String args[]) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- != 0){            int n = s.nextInt();            int k = s.nextInt();            int times = n/k;            int rem = n % k;            char c = 'a';            while(k > 0){                for(int i = 0; i < times; i++){                    System.out.print(c);                }                k--;                c++;            }            while(rem > 0){                System.out.print('a');                rem--;            }            System.out.println();        }    }}
