 import java.util.*;import java.io.*;  public class B {        public static void taskSolver(FastReader sc){    int n=sc.nextInt();     int arr[]=new int[n];     for(int i=0;i<n;i++)arr[i]=sc.nextInt();     int ans=0;     Arrays.sort(arr);     for(int i=1;i<=n;i+=2){        ans+=Math.abs(arr[i]-arr[i-1]);      }     System.out.println(ans);    }    public static void main(String args[]){       FastReader sc=new FastReader();               taskSolver(sc);         }     public static long gcd(long a,long b){          if(a==0)return b;         return gcd(b%a,a);      }    static class FastReader     {         BufferedReader br;         StringTokenizer st;           public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }           String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }           int nextInt()         {             return Integer.parseInt(next());         }           long nextLong()         {             return Long.parseLong(next());         }           double nextDouble()         {             return Double.parseDouble(next());         }           String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     }         }   
