import java.io.*;import java.util.*;public class Main {	static BufferedReader in = new BufferedReader(new InputStreamReader(System.in));	static PrintWriter out = new PrintWriter(new OutputStreamWriter(System.out));	public static void main(String[] args) throws IOException {		String s = in.readLine();		String [] ss = in.readLine().split(" ");		for(int i=0;i<ss.length;i++) {			if(s.charAt(0)==ss[i].charAt(0)||s.charAt(0)==ss[i].charAt(1)) {				out.println("YES");				break;			}			if(s.charAt(1)==ss[i].charAt(0)||s.charAt(1)==ss[i].charAt(1)) {				out.println("YES");				break;			}			if(i == ss.length-1 ) out.println("NO");		}		out.close();	}}
