import java.io.*;import java.util.Scanner;public class code_11A{    public static void main(String args[])throws IOException    {        Scanner in=new Scanner(System.in);        int n=in.nextInt();        int d=in.nextInt();                int x[]=new int[n];        for(int i=0;i<n;i++)            x[i]=in.nextInt();        int i=1,c=0;        while(i<n)        {            if(x[i-1]<x[i])                i++;                        else if(x[i]<=x[i-1])            {                double z=(double)((x[i-1]-x[i])/(double)d);                                if(z==0.0)                    z=1.0;                                c+=(int)(Math.ceil(z));                                  x[i]+=(d*((int)(Math.ceil(z))));                if(x[i]==x[i-1])                {                    c+=1;                    x[i]+=d;                }                                                   i++;            }        }        System.out.println(c);    }}
