import java.util.*;import java.io.*; public class Main{    public static void main(String[] args) throws java.lang.Exception    {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        String s=(br.readLine());        int c=0;                  for(int i=0;i<s.length();i++){            if(s.charAt(i)=='4'||s.charAt(i)=='7'){                    c++;                }            }            if(c==4 || c==7){                System.out.println("YES");            }            else{                System.out.println("NO");            }    }    }
