import java.util.*;import java.math.*;public class MinimumInteger{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int runs = sc.nextInt();			while(runs-->0)			{				double l = sc.nextDouble();				double r = sc.nextDouble();				double x = sc.nextDouble();				if(x<l||x>r)				{					System.out.println((long)x);				}				else				{					if(r%x==0)						r++;					System.out.println((long)(x*Math.ceil(r/x)));				}			}		}}
