// Don't place your source in a packageimport java.lang.reflect.Array;import java.text.DecimalFormat;import java.util.*;import java.lang.*;import java.io.*;import java.math.*;        // Please name your class Mainpublic class Main {    static FastScanner fs=new FastScanner();    static class FastScanner {//scanner from SecondThread        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st=new StringTokenizer("");        public String next() {            while (!st.hasMoreElements())                try {                    st=new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            return st.nextToken();        }        int Int() {            return Integer.parseInt(next());        }         long Long() {            return Long.parseLong(next());        }         String Str(){            return next();        }    }      public static void main (String[] args) throws java.lang.Exception {        PrintWriter out = new PrintWriter(System.out);          int T=1;        for(int t=0;t<T;t++){            int n=Int();int k=Int();            int A[]=new int[n];            for(int i=0;i<n;i++){                A[i]=Int();            }            Sol sol=new Sol();            sol.solution(out,A,k);        }        out.flush();     }     public static int Int(){        return fs.Int();    }    public static long Long(){        return fs.Long();    }    public static String Str(){        return fs.Str();    } }   class Sol{    public void solution(PrintWriter out,int A[],int k){        if(A.length<k){            out.println("NO");            return;        }        Map<Integer,List<Integer>>f=new HashMap<>();        for(int i=0;i<A.length;i++){            if(!f.containsKey(A[i]))f.put(A[i],new ArrayList<>());            f.get(A[i]).add(i);        }         for(Integer key:f.keySet()){            if(f.get(key).size()>k){                out.println("NO");                return;            }        }         int res[]=new int[A.length];        int ith=0;        for(Integer key:f.keySet()){            List<Integer>list=f.get(key);            for(int index:list){                res[index]=ith+1;                ith++;                ith%=k;            }        }         out.println("YES");        for(int i:res){            out.print(i+" ");        }    }        }      
