//package homework; import java.util.Scanner; public class SalemAndSticks { 	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int n = in.nextInt();		int [] a = new int [n];		int t = 0; int min = 10000000; int counter = 0; int cost = 10000000;				for (int i = 0; i < n; i++) {			int x = in.nextInt();			a[i] = x;					}		for (int i = 0; i < 101; i++) {			for (int j = 0; j < n; j++) {			if (Math.abs(a[j] - i) > 1)	{counter += Math.abs(a[j] - i)-1;}						}			min = Math.min(min, counter);			if (counter == min) {t = i; cost = counter;}						counter = 0;					}				System.out.println(t + " " + cost);	} }
