import java.util.*;public class Main{	public static void main(String[] args)	{		int n,x=0;		Scanner reader=new Scanner(System.in);		n=reader.nextInt();		String[]s=new String[n];		int i,j,t=0;		for(i=0;i<n;++i)			s[i]=reader.next();		for(i=1;i<n-1;++i)		{			for(j=1;j<n-1;++j)			{		        if(s[i].charAt(j)=='X'&&s[i].charAt(j)==s[i-1].charAt(j-1)&&s[i].charAt(j)==s[i-1].charAt(j+1)&&s[i].charAt(j)==s[i+1].charAt(j+1)&&s[i+1].charAt(j-1)==s[i].charAt(j))		        	++t;			}		}		System.out.println(t);	}}  					   			    						 				
