//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int t = Integer.parseInt(br.readLine());        while(t-->0)        {            String st[] = br.readLine().split(" ");            int l1 = Integer.parseInt(st[0]),r1 = Integer.parseInt(st[1]),l2 = Integer.parseInt(st[2]),r2 = Integer.parseInt(st[3]);            System.out.println(l1==l2? (l1+" "+r2): (l1+" "+l2));        }    }}
