import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.Collections;import java.util.StringTokenizer; public class B { 	@SuppressWarnings("deprecation")	public static void main(String[] args) {		FastScanner fs=new FastScanner();		int t=fs.nextInt();		int max = 1;		int x = 1;		ArrayList<Integer> a = new ArrayList<>();		while(t-->0)		{			int n = fs.nextInt();			a.add(n);			max = Math.max(max, n);					}		int m = max;		int i = 2;		for (int j = 1;j  <= m; ++j) {		      if (m% j == 0) {		    	  a.remove(new Integer(j)); 		      }		}		Collections.sort(a);		x = a.remove(a.size()-1);		System.out.println(x+" "+max); 	} 	static class FastScanner {		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st=new StringTokenizer("");		String next() {			while (!st.hasMoreTokens())				try {					st=new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			return st.nextToken();		} 		int nextInt() {			return Integer.parseInt(next());		}		int[] readArray(int n) {			int[] a=new int[n];			for (int i=0; i<n; i++)				a[i]=nextInt();			return a;		}		long nextLong() {			return Long.parseLong(next());		}	} }
