//package com.company; import java.util.HashMap;import java.util.Scanner; public class CF_1109_A {    public static void main(String args[]) {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int a[]=new int[n];        for(int i=0;i<n;++i)            a[i]=sc.nextInt();        HashMap<Integer,Integer> hm[]=(HashMap<Integer,Integer>[])new HashMap[2];        hm[0]=new HashMap<>();        hm[1]=new HashMap<>();//        int dp[]=new int[n+1];//        for(int i=0;i<n;++i)//            dp[i+1]=dp[i]^a[i];        hm[0].put(0,1);        long ans=0;        int xor=0;        for(int i=0;i<a.length;++i) {            xor^=a[i];            ans+=hm[(i+1)%2].getOrDefault(xor,0);            hm[(i+1)%2].put(xor,hm[(i+1)%2].getOrDefault(xor,0)+1);        }        System.out.println(ans);    }}
